mapper.select:
SELECT pwr_id, member_id, selling_area_id, title, status, sell_price, view_count, description, created_at, is_direct, is_delivery, sup_category, sub_category FROM post WHERE 1=1
AND category = #{category}
AND ( title LIKE CONCAT('%', #{keyword}, '%') OR description LIKE CONCAT('%', #{keyword}, '%') OR selling_area_id LIKE CONCAT('%', #{keyword}, '%') OR sup_category LIKE CONCAT('%', #{keyword}, '%') OR sub_category LIKE CONCAT('%', #{keyword}, '%') )
